preConditions.sqlCheck:
select count(*) from
clinlims.system_role
where name = 'Reception';

select count(*) from
clinlims.system_role
where name = 'Lab Unit Roles' limit 1;

changeSet.sql:
insert into clinlims.system_role values(nextval('clinlims.system_role_seq'),'Reception','Sample entry and patient management.',FALSE,(select id from clinlims.system_role where name = 'Lab Unit Roles'),'role.intake',TRUE,FALSE);
update clinlims.system_role set active = TRUE where name in ('Global Roles','Lab Unit Roles');
update clinlims.system_role set grouping_parent = (select id from clinlims.system_role where name = 'Global Roles') where name = 'Audit Trail';

